import React, { useState, useEffect, useRef } from "react";

export const LyricTypingInput = ({ lines }) => {
  // Initialize state to store user inputs
  const [inputs, setInputs] = useState(
    lines.map((line) => line.split(" ").map(() => ""))
  );

  const inputRefs = useRef([]);

  const handleChange = (e, lineIndex, wordIndex) => {
    const { value } = e.target;
    const newInputs = inputs.map((line, li) =>
      line.map((word, wi) => {
        if (li === lineIndex && wi === wordIndex) {
          return value;
        }
        return word;
      })
    );
    setInputs(newInputs);

    // Move to next input if current input is full
    if (value.length >= e.target.maxLength) {
      const nextInput = inputRefs.current[lineIndex]?.[wordIndex + 1];
      if (nextInput) {
        nextInput.focus();
      }
    }
  };

  useEffect(() => {
    // Initialize inputRefs.current as a 2D array
    inputRefs.current = lines.map(() => []);
  }, [lines]);

  return (
    <div>
      <h1>balls lol</h1>
      {lines.map((line, lineIndex) => (
        <div key={lineIndex} style={{ marginBottom: "20px" }} className="test">
          <textarea
            rows={1}
            ref={(el) => (inputRefs.current[lineIndex][0] = el)} // Set reference for the first input in the line
            onChange={(e) => handleChange(e, lineIndex, 0)} // Handle change on the first textarea
            style={{
              width: `${line.length * 15}px`, // Adjust width based on line length (15px per character)
              margin: "0 5px",
              textAlign: "left",
              border: "none",
              borderBottom: "2px solid #000", // Add bottom border to simulate underline
              outline: "none",
              backgroundColor: "transparent",
              resize: "none", // Prevent resizing
              overflow: "hidden", // Hide overflow
              padding: "2px 0",
            }}
            maxLength={line.length}
            onInput={(e) => {
              e.target.style.height = "auto"; // Reset height
              e.target.style.height = `${e.target.scrollHeight}px`; // Set height to scrollHeight to auto-expand
            }}
          />
        </div>
      ))}
    </div>
  );
};
