const puppeteer = require("puppeteer");

const Musix = async () => {
  const lyricsArray = [];
  const browser = await puppeteer.launch({
    headless: false,
    defaultViewport: false,
  });
  const page = await browser.newPage();
  await page.goto(
    "https://www.musixmatch.com/lyrics/Grupo-Marca-Registrada-Grupo-Frontera/Di-Que-Si/translation/english"
  );

  const lyricsContainers = await page.$$(
    ".css-1rynq56.r-1grxjyw.r-adyw6z.r-11rrj2j.r-13awgt0.r-ueyrd6.r-fdjqy7"
  );

  //   const translation = await page.$$(
  //     ".css-1rynq56.r-13awgt0.r-ubezar.r-vrz42v.r-11wrixw.r-fdjqy7"
  //   );

  for (const line of lyricsContainers) {
    const lines = await page.evaluate((el) => el.textContent, line);
    lyricsArray.push(lines.trim());
    //console.log(lines.trim());
  }

  //   if (translation.length === 0) {
  //     console.log("Translation elements not found on the page.");
  //   } else {
  //     for (const line of translation) {
  //       console.log("working");
  //       const lines = await page.evaluate((el) => el.textContent, line);
  //       console.log(lines.trim());
  //     }
  //   }

  await browser.close();

  return lyricsArray
};

const LyricFind = async () => {
  const browser = await puppeteer.launch({
    headless: false,
    defaultViewport: false,
  });
  const page = await browser.newPage();
  await page.goto("https://lyrics.lyricfind.com/lyrics/peso-pluma-gavilan-ii");

  const lyricsContainers = await page.$$(".MuiBox-root.css-165casq");
  for (const container of lyricsContainers) {
    const lines = await container.$$(".MuiBox-root.css-0 > div"); // Adjusted selector
    for (const line of lines) {
      const text = await page.evaluate((el) => el.textContent, line);
      console.log(text.trim());
    }
    console.log(""); // Empty line to separate verses or sections
  }

  await browser.close();
};

Musix();

